suite('Cloud events');

platform('gen3', 'gen4');

let deviceId;
let maxEventDataSize = 0;

function parseDataUri(str) {
	const m = /^data:(.+?)(;base64)?,(.*)$/.exec(str);
	if (!m) {
		throw new Error('Failed to parse data URI');
	}
	let data;
	const base64 = !!m[2];
	if (base64) {
		data = Buffer.from(m[3], 'base64');
	} else {
		data = decodeURI(m[3]);
	}
	return {
		type: m[1],
		data
	};
}

